[
  {
    "id": "costo_base_paese",
    "expr": "if(paese_categoria==1,7000, if(paese_categoria==2,8500, if(paese_categoria==3,11000, if(paese_categoria==4,9500,9000))))"
  },
  { "id": "costo_procedura",   "expr": "costo_ente + costo_base_paese + altre_spese_documentate" },
  { "id": "costo_viaggio",     "expr": "durata_soggiorno_giorni * costo_giornaliero_persona * numero_viaggiatori" },
  { "id": "costo_totale",      "expr": "costo_procedura + costo_viaggio" },
  { "id": "beneficio_fiscale", "expr": "costo_totale * 0.5" },
  { "id": "costo_netto",       "expr": "costo_totale - beneficio_fiscale" },
  { "id": "tempo_min_mesi",    "expr": "if(tipo_adozione==1,36,36)" },
  { "id": "tempo_max_mesi",    "expr": "if(tipo_adozione==1,48,60)" }
]
